import React, { Component } from "react";
import axios from "axios";

export default class EditTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params,
      tour: ""
    };
  }
  componentDidMount() {
    axios
      .get("http://127.0.0.1:8000/api//tours/gettour/" + this.state.id)
      .then(res => {
        console.log(res.data);
        this.setState({
          tour: res.data
        });
      });
  }
  render() {
    return (
      <div>
        <label>Tour</label>
        <input placeholder="tour" value={this.state.tour.tour} />
      </div>
    );
  }
}
