import React, { Component } from "react";
import axios from "axios";

class EditTour extends Component {
  static propTypes = {
    match: PropTypes.shape({
      params: PropTypes.object,
      isExact: PropTypes.bool,
      path: PropTypes.string,
      url: PropTypes.string
    })
  };
  constructor(props) {
    super(props);
    this.state = {
      tour: {}
    };
  }
  componentDidMount() {
    const { id } = this.props.match.params;
    this.bucarDatos(id);
  }

  bucarDatos(id) {
    axios.get(`http://127.0.0.1:8000/api/tours/gettour/${id}`).then(res => {
      console.log(res.data);
      this.setState({
        tour: res.data
      });
    });
  }

  render() {
    const { tour, codigo_id } = this.state.tour;
    return (
      <div>
        <label>Tour</label>
        <input placeholder="tour" type="text" value={tour} />
        <input placeholder="codigo" type="text" value={codigo_id} />
      </div>
    );
  }
}

export default EditTour;
