import React, { Component } from "react";
import axios from "axios";

export class EditTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      datos: {
        tour: "",
        codigo_id: "",
        descripcion: "",
        itinerario: "",
        incluye: "",
        dias: "",
        status: "",
        id: ""
      }
    };
  }
  componentDidMount() {
    const { id } = this.props.match.params;
    this.bucarDatos(id);
  }

  bucarDatos(id) {
    axios.get(`http://127.0.0.1:8000/api/tours/gettour/${id}`).then(res => {
      console.log(res.data);
      const datos = res.data;
      this.setState({
        datos: datos
      });
      console.log("estado", this.state.datos);
    });
  }

  actualizarNombreTour = e => {
    this.setState({
      datos: {
        tour: e.target.value
      }
    });
    console.log("nuevo estado", this.state.datos);
  };

  cambiosTour = e => {
    const { id } = this.props.match.params;

    axios
      .post(`http://127.0.0.1:8000/api/tours/update/${id}`, {
        nombre: this.state.datos.tour,
        codigo: this.state.datos.codigo_id,
        descripcion: this.state.datos.descripcion,
        itinerario: this.state.datos.itinerario,
        incluye: this.state.datos.incluye,
        dias: this.state.datos.dias
      })
      .then(res => {
        console.log(res.data);
      });
  };

  render() {
    const { tour } = this.state.datos;
    return (
      <div>
        <label>Tour</label>
        <input
          id="tour"
          name="tour"
          placeholder="tour"
          type="text"
          value={tour}
          onChange={this.actualizarNombreTour}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={tour}
          onChange={this.actualizarNombreTour}
        />

        <button onClick={this.cambiosTour}>Guardad cambios</button>
      </div>
    );
  }
}
