import React, { Component } from "react";
import axios from "axios";

export class EditTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tour: "",
      codigo_id: "",
      descripcion: "",
      itinerario: "",
      incluye: "",
      dias: "",
      status: "",
      id: ""
    };
  }
  componentDidMount() {
    console.log(this.state.datos);
    const { id } = this.props.match.params;
    this.bucarDatos(id);
  }

  bucarDatos(id) {
    axios.get(`http://127.0.0.1:8000/api/tours/gettour/${id}`).then(res => {
      console.log(res.data);
      this.setState({
        tour: res.data.tour,
        codigo_id: res.data.codigo_id,
        descripcion: res.data.descripcion,
        itinerario: res.data.itinerario,
        incluye: res.data.incluye,
        dias: res.data.dias,
        status: res.data.status,
        id: res.data.id
      });
      console.log("estado", this.state);
    });
  }

  cambiosTour = e => {
    const { id } = this.props.match.params;

    axios
      .post(`http://127.0.0.1:8000/api/tours/update/${id}`, {
        nombre: this.state.tour,
        codigo: this.state.codigo_id,
        descripcion: this.state.descripcion,
        itinerario: this.state.itinerario,
        incluye: this.state.incluye,
        dias: this.state.dias
      })
      .then(res => {
        console.log(res.data);
      });
  };

  actualizarNombreTour = e => {
    this.setState(
      {
        tour: e.target.value
      },
      () => console.log("nuevo estado", this.state)
    );
  };

  actualizarCodigo = e => {
    this.setState(
      {
        codigo_id: e.target.value
      },
      () => console.log("nuevo estado", this.state)
    );
  };

  render() {
    const { tour, codigo_id } = this.state;
    return (
      <div>
        <label>Tour</label>
        <input
          id="tour"
          name="tour"
          placeholder="tour"
          type="text"
          value={tour}
          onChange={this.actualizarNombreTour}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={codigo_id}
          onChange={this.actualizarCodigo}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={codigo_id}
          onChange={this.actualizarCodigo}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={codigo_id}
          onChange={this.actualizarCodigo}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={codigo_id}
          onChange={this.actualizarCodigo}
        />
        <input
          id="codigo_id"
          name="codigo_id"
          placeholder="codigo_id"
          type="text"
          value={codigo_id}
          onChange={this.actualizarCodigo}
        />

        <button onClick={this.cambiosTour}>Guardad cambios</button>
      </div>
    );
  }
}
