import React, { Component } from "react";
import axios from "axios";

class EditTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tour: {}
    };
  }
  componentDidMount() {
    const { id } = this.props.match.params;
    this.bucarDatos(id);
  }

  bucarDatos(id) {
    axios.get(`http://127.0.0.1:8000/api/tours/gettour/${id}`).then(res => {
      console.log(res.data);
      this.setState({
        tour: res.data
      });
    });
  }

  cambiosTour() {
    alert("datos");
  }

  render() {
    const { tour, codigo_id } = this.state.tour;
    return (
      <div>
        <label>Tour</label>
        <input placeholder="tour" type="text" value={tour} />
        <input placeholder="codigo" type="text" value={codigo_id} />
        <button onClick={this.cambiosTour}>Guardad cambios</button>
      </div>
    );
  }
}

export default EditTour;
