import React, { Component } from "react";
import axios from "axios";

export class EditTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tour: {}
    };
  }
  componentDidMount() {
    const { id } = this.props.match.params;
    this.bucarDatos(id);
  }

  bucarDatos(id) {
    axios.get(`http://127.0.0.1:8000/api/tours/gettour/${id}`).then(res => {
      console.log(res.data);
      const tour = res.data;
      this.setState({
        tour: tour
      });
    });
  }

  actualizarNombreTour = e => {
    const { tour } = this.state.tour;
    this.setState({
      tour: e.target.value,
      codigo_id: e.target.value
    });
  };

  cambiosTour = e => {
    const { id } = this.props.match.params;

    axios
      .post(`http://127.0.0.1:8000/api/tours/update/${id}`, {
        nombre: this.state.tour,
        codigo: this.state.codigo_id,
        descripcion: "ala ñonga",
        itinerario: "no ahi",
        incluye: "nda",
        dias: "unes"
      })
      .then(res => {
        console.log(res.data);
      });
  };

  render() {
    const { tour, codigo_id } = this.state.tour;
    return (
      <div>
        <label>Tour</label>
        <input
          placeholder="tour"
          type="text"
          value={tour}
          onChange={this.actualizarNombreTour}
        />
        <input
          placeholder="codigo"
          type="text"
          value={codigo_id}
          onChange={this.actualizarNombreTour}
        />
        <button onClick={this.cambiosTour}>Guardad cambios</button>
      </div>
    );
  }
}
